import React from "react";
import alunos from "../../Data/alunos"
export default function alu(props){
    {/*
    pra apenas um 
    
    const li1 = (
         <li>
                {alunos[0].id})  {alunos[0].nome} -> {alunos[0].nota}
         </li>
    );*/}

    const lis = alunos.map(function liss(aluno) {
        return (
            <li>
                {aluno.id}) {aluno.nome} -> {aluno.nota}
            </li>
        )
    })
    return (
        <div>
            <ul>
               {lis}
            </ul>
        </div>
    )
}
